function switchForm() {
    $("#autocomplete").toggle();
    $("#normal").toggle();
    var num_friends = window.globaldata.length;
    var select = document.getElementById("friends");
    for (var i=0; i < num_friends; i++) {
        var option = document.createElement("option");
        option.innerHTML = window.globaldata[i];
        option.value = window.fids[i];
        select.appendChild(option);
    }
};

